package org.mineid.authserver;

import org.jetbrains.annotations.Contract;
import org.jetbrains.annotations.NotNull;

public final class Messages {
    @Contract(pure = true)
    public static @NotNull String getStatus(int version) {
        return "{\"version\":{\"name\":\"MineAuth v1\",\"protocol\":" + version + "},\"players\":{\"max\": 1,\"online\":0,\"sample\":[]},\"description\":{\"text\":\"\u00a78\u00a7l    \u2582\u00a7f \u2583 \u00a77\u2585\u00a72 \u2586\u00a7f \u2587 \u00a7a\u2588\u00a7a   MineID.org\u00a7a   \u2588\u00a7f \u2587 \u00a72\u2586\u00a77 \u2585\u00a7f \u2583 \u00a78\u2582\u00a7r  \n\u00a7a            Join to grab your security code!\"},\"favicon\":\"data:image/png;base64,\"}";
    }

    public static @NotNull String getStatusOutdated(int version) {
        return "{\"version\":{\"name\":\"MineAuth v1\",\"protocol\":" + version + "},\"players\":{\"max\": 1,\"online\":0,\"sample\":[]},\"description\":{\"text\":\"\u00a78\u00a7l    \u2582\u00a7f \u2583 \u00a77\u2585\u00a72 \u2586\u00a7f \u2587 \u00a7a\u2588\u00a7a   MineID.org\u00a7a   \u2588\u00a7f \u2587 \u00a72\u2586\u00a77 \u2585\u00a7f \u2583 \u00a78\u2582\u00a7r  \n\u00a74           Join with a 1.8+ Minecraft client!\"},\"favicon\":\"data:image/png;base64,\"}";
    }

    public static @NotNull String getVerificationMessage(String code) {
        return "[\"\",{\"text\":\"\u2582\",\"bold\":true,\"color\":\"dark_gray\"},{\"text\":\" \u2583 \"},{\"text\":\"\u2585\",\"color\":\"gray\"},{\"text\":\" \u2586\",\"color\":\"dark_green\"},{\"text\":\" \u2587 \"},{\"text\":\"\u2588\",\"color\":\"green\"},{\"text\":\" \u0020 MineID.org\",\"color\":\"green\"},{\"text\":\" \u0020 \u2588\",\"color\":\"green\"},{\"text\":\" \u2587 \"},{\"text\":\"\u2586\",\"color\":\"dark_green\"},{\"text\":\" \u2585\",\"color\":\"gray\"},{\"text\":\" \u2583 \"},{\"text\":\"\u2582\",\"color\":\"dark_gray\"},{\"text\":\"\n\\nTo \"},{\"text\":\"verify\",\"color\":\"green\"},{\"text\":\" your account, please\ntype in your \"},{\"text\":\"security code\",\"color\":\"green\"},{\"text\":\" below:\n\n\"},{\"text\":\"" + code + "\",\"bold\":true,\"color\":\"green\"},{\"text\":\"\n\n\"},{\"text\":\"Do not share this code\",\"bold\":true,\"color\":\"red\"},{\"text\":\"\n\n\"},{\"text\":\"If this code was shown to someone\nthat is not you, please join\n\",\"color\":\"red\"},{\"text\":\"rescue.mineid.org\",\"bold\":true,\"color\":\"red\"},{\"text\":\" to save your account.\",\"color\":\"red\"}]";
    }
}
